#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\laragon\www\TMSADM\frontend\node_modules\.pnpm\toast@0.5.4_react@18.3.1\node_modules\toast\node_modules;C:\laragon\www\TMSADM\frontend\node_modules\.pnpm\toast@0.5.4_react@18.3.1\node_modules;C:\laragon\www\TMSADM\frontend\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/laragon/www/TMSADM/frontend/node_modules/.pnpm/toast@0.5.4_react@18.3.1/node_modules/toast/node_modules:/mnt/c/laragon/www/TMSADM/frontend/node_modules/.pnpm/toast@0.5.4_react@18.3.1/node_modules:/mnt/c/laragon/www/TMSADM/frontend/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/NODE_OPTIONS='--experimental-loader="toast/src/loader.mjs"'$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/NODE_OPTIONS='--experimental-loader="toast/src/loader.mjs"'$exe"  node "$basedir/../../toast-cli.mjs" $args
  } else {
    & "$basedir/NODE_OPTIONS='--experimental-loader="toast/src/loader.mjs"'$exe"  node "$basedir/../../toast-cli.mjs" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "NODE_OPTIONS='--experimental-loader="toast/src/loader.mjs"'$exe"  node "$basedir/../../toast-cli.mjs" $args
  } else {
    & "NODE_OPTIONS='--experimental-loader="toast/src/loader.mjs"'$exe"  node "$basedir/../../toast-cli.mjs" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
